import React, {useState} from "react"
import Data from "./Data"

function App() {
  const [query, setQuery] = useState("");

  return (
    <div className="App">
      <input placeholder="Search" onChange={(e) => setQuery(e.target.value)} />

      {
        Data.filter((person) => {
          if (query === "") {
            return person;
          } else if (person.name.toLowerCase().includes(query.toLowerCase())) {
            return person;
          }
        }).map((person, key) => {
          return (
            <div key={key}>
              <p>{person.name}</p>
            </div>
          )
        })
      }

    </div>
  )

}

export default App;
